'use client';

import { useState, useRef } from 'react';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import { Trash2, PlusCircle } from 'lucide-react';

export default function Home() {
  const [customerName, setCustomerName] = useState('');
  const [customerVat, setCustomerVat] = useState('');
  const [invoiceNumber, setInvoiceNumber] = useState('');
  const [date, setDate] = useState('');
  const [vatIncluded, setVatIncluded] = useState(false);
  const [services, setServices] = useState([
    { description: '', quantity: '', unitPrice: '' },
  ]);
  const arabicImgRef = useRef<HTMLImageElement>(null);

  const handleServiceChange = (index: number, field: string, value: string) => {
    const updated = [...services];
    updated[index][field as keyof typeof updated[0]] = value;
    setServices(updated);
  };

  const handleAddService = () => {
    setServices([...services, { description: '', quantity: '', unitPrice: '' }]);
  };

  const handleRemoveService = (index: number) => {
    const updated = services.filter((_, i) => i !== index);
    setServices(updated);
  };

  const handleDownload = async () => {

    
    const parsed = services.map((s, i) => {
      const qty = parseFloat(s.quantity || '0');
      const rate = parseFloat(s.unitPrice || '0');
      const amount = qty * rate;
      return {
        sno: i + 1,
        description: s.description,
        quantity: qty,
        unitPrice: rate.toFixed(3),
        amount: amount.toFixed(3),
      };
    });

    const subtotal = parsed.reduce((acc, cur) => acc + parseFloat(cur.amount), 0);
    const vat = vatIncluded ? subtotal * 0.05 : 0;
    const grandTotal = subtotal + vat;

    const doc = new jsPDF();

    // Company address on the left side
    doc.setFontSize(10).setFont('helvetica', 'normal');

    doc.text('C.R. No: 1441300', 14, 15);
    doc.text('P.O. Box: 150', 14, 20);
    doc.text('Postel Code: 117', 14, 25);
    doc.text('Muscat, Sultanate of Oman', 14, 30);
    doc.text('Phone: +968 78923239', 14, 35);
    doc.text('VATIN No: OM1100320427', 14, 40);

    // Company name in center
    doc.setFontSize(18).setFont('helvetica', 'bold');
    doc.setTextColor(23, 35, 54); // Reset to black
    doc.text('RAMESH INTERNATIONAL', 105, 15, { align: 'center' });

    // Company email
    doc.setFontSize(11).setFont('helvetica', 'normal');
    doc.setTextColor(23, 35, 54); // Reset to black
    doc.text('ramesh@rameshinternational.com', 105, 22, { align: 'center' });

    // Arabic image on the right (you must have the image loaded)
    const base64ArabicImage = 'data:image/png;base64,'; // your full base64 string here

    // Add image to the right side of header
    doc.addImage(base64ArabicImage, 'PNG', 165, 10, 30, 30);
    

    doc.setFontSize(14).setFont('helvetica', 'bold');
    doc.text('INVOICE', 105, 35, { align: 'center' });
    doc.line(95, 37, 115, 37);

    doc.setFontSize(14).setFont('helvetica', 'bold');
    doc.setTextColor(255, 0, 0);
    doc.text(`No: ${invoiceNumber || '..............'}`, 14, 52);

    // Add space below the title
    const customerRowY = 62;

    doc.setFontSize(11).setFont('helvetica', 'normal');
    doc.setTextColor(23, 35, 54); // Reset to black
    doc.text(`Customer Name: Mr. / M/s ${customerName || '............................'}`, 14, customerRowY);
    if (vatIncluded && customerVat) {
      doc.setTextColor(23, 35, 54); // Reset to black

      doc.text(`VATIN No: ${customerVat}`, 14, 68);
    }
    doc.setTextColor(23, 35, 54); // Reset to black

    doc.text(`Date: ${date || '............'}`, 160, customerRowY);

    autoTable(doc, {
      startY: vatIncluded && customerVat ? 75 : 70,
      head: [['S No.', 'Description', 'Qty.', 'Unit Rate', 'Amount']],
      body: parsed.map((s) => [
        s.sno.toString(),
        s.description,
        s.quantity.toString(),
        s.unitPrice,
        s.amount,
      ]),
      styles: { fontSize: 10,
        textColor: [23, 35, 54],
        
       },
      headStyles: { fillColor: [220, 220, 220],
        fontStyle: 'bold',
       },
    });

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const finalY = (doc as any).lastAutoTable?.finalY || 80;

    autoTable(doc, {
      startY: finalY + 10,
      body: [
        ['Total', subtotal.toFixed(3)],
        ['VAT (5%)', vat.toFixed(3)],
        ['Grand Total', grandTotal.toFixed(3)],
      ],
      tableLineWidth: 0,
      styles: {
        halign: 'right',
        textColor: [0, 0, 255],
        fontStyle: 'bold',
        fontSize: 10,
        cellPadding: { top: 2, right: 6, bottom: 2, left: 6 },
      },
      columnStyles: {
        0: { cellWidth: 35 },
        1: { cellWidth: 35 },
      },
      margin: { left: 125 },
    });




    const bankNoteStart = finalY + 45;
    doc.setFontSize(10);
    doc.setTextColor(23, 35, 54); // Reset to black

    doc.text(
      'For bank transfers, please use the following account details and include the invoice number as the payment reference:',
      14,
      bankNoteStart
    );
    doc.text('Bank Name: BANK MUSCAT', 14, bankNoteStart + 8);
    doc.text('Account Name: RAMESH INTERNATIONAL SPC', 14, bankNoteStart + 15);
    doc.text('Account Number: 0317072830880018', 14, bankNoteStart + 22);
    doc.text('Branch: Al Khuwair', 14, bankNoteStart + 29);


    const stampImg = 'data:image/png;base64,';

    // This comes after the last bank detail line
    const stampY = bankNoteStart + 0; // Adjust as needed

    doc.addImage(stampImg, 'PNG', 100, stampY, 100, 100); // x, y, width, height



    doc.save(`invoice_${customerName || 'bill'}.pdf`);
  };

  const calculateAmount = (qty: string, rate: string) => {
    const q = parseFloat(qty || '0');
    const r = parseFloat(rate || '0');
    return (q * r).toFixed(3);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-white to-gray-100 text-black px-4 py-6 max-w-4xl mx-auto">
      <img src="/arabic.png" alt="Arabic Label" ref={arabicImgRef} className="hidden" />

      <h2 className="text-3xl font-bold text-center mb-1"> Bill Generator</h2>
      <p className="font-bold text-center mb-6">RAMESH INTERNATIONAL</p>

      <div className="grid sm:grid-cols-2 gap-4 mb-6">
        <div>
          <label className="block text-sm font-semibold mb-1"> Customer Name</label>
          <input
            type="text"
            value={customerName}
            onChange={(e) => setCustomerName(e.target.value)}
            className="w-full border rounded px-3 py-2"
            placeholder="Enter name"
          />
        </div>
        
        <div>
          <label className="block text-sm font-semibold mb-1"> Invoice Number</label>
          <input
            type="text"
            value={invoiceNumber}
            onChange={(e) => setInvoiceNumber(e.target.value)}
            className="w-full border rounded px-3 py-2"
            placeholder="Enter invoice number"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold mb-1"> Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border rounded px-3 py-2"
          />
        </div>
        {vatIncluded && (
          <div className="sm:col-span-2">
            <label className="block text-sm font-semibold mb-1"> Customer VAT Number</label>
            <input
              type="text"
              value={customerVat}
              onChange={(e) => setCustomerVat(e.target.value)}
              className="w-full border rounded px-3 py-2"
              placeholder="Enter VAT Number"
            />
          </div>
        )}
      </div>

      <h2 className="text-xl font-semibold mb-2"> Services</h2>

      {services.map((service, index) => (
        <div
          key={index}
          className="grid grid-cols-1 sm:grid-cols-6 gap-2 items-center mb-3 bg-white p-3 rounded shadow-sm"
        >
          <input
            type="text"
            placeholder="Description"
            value={service.description}
            onChange={(e) => handleServiceChange(index, 'description', e.target.value)}
            className="sm:col-span-2 border rounded px-2 py-1"
          />
          <input
            type="number"
            placeholder="Qty"
            value={service.quantity}
            onChange={(e) => handleServiceChange(index, 'quantity', e.target.value)}
            className="border rounded px-2 py-1"
          />
          <input
            type="number"
            placeholder="Unit Price"
            value={service.unitPrice}
            onChange={(e) => handleServiceChange(index, 'unitPrice', e.target.value)}
            className="border rounded px-2 py-1"
          />
          <div className="text-right font-medium">
            {calculateAmount(service.quantity, service.unitPrice)}
          </div>
          <button
            onClick={() => handleRemoveService(index)}
            className="text-red-600 hover:text-red-800"
            title="Remove"
          >
            <Trash2 size={20} />
          </button>
        </div>
      ))}

      <button
        onClick={handleAddService}
        className="flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 mb-4"
      >
        <PlusCircle size={18} />
        Add Service
      </button>

      <div className="mb-6">
        <label className="inline-flex items-center space-x-2">
          <input
            type="checkbox"
            checked={vatIncluded}
            onChange={(e) => setVatIncluded(e.target.checked)}
            className="accent-green-600"
          />
          <span className="font-medium">Include VAT (5%)</span>
        </label>
      </div>

      <button
        onClick={handleDownload}
        className="w-full sm:w-auto bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700"
      >
         Download PDF
      </button>
    </div>
  );
}
